import { useState, useMemo } from "react";
import "./styles.css";

export default function ListingAd({
  flag,
  arrow_left,
  arrow_right,
  price,
  icon,
  pic,
  title,
  address,
  description,
  availabilities_label,
  subprice_label,
  project_type,
  year,
  ownership_type
}) {
  // State untuk mengontrol visibilitas deskripsi
  const [isDescriptionVisible, setIsDescriptionVisible] = useState(false);
  // State untuk mengontrol apakah nomor telepon asli ditampilkan
  const [showRealNumbers, setShowRealNumbers] = useState(false);

  // Fungsi untuk mengubah deskripsi menjadi anonim, dibungkus dengan useMemo
  const anonymizedDescription = useMemo(() => {
    // Regex untuk menemukan nomor telepon 8 digit dan mengganti 4 digit terakhir
    return description.replace(/\b(\d{4})\d{4}\b/g, "$1 XXXX");
  }, [description]);

  // Handler untuk tombol utama
  const toggleDescription = () => {
    // Jika deskripsi akan disembunyikan, reset juga status nomor telepon
    if (isDescriptionVisible) {
      setShowRealNumbers(false);
    }
    setIsDescriptionVisible(!isDescriptionVisible);
  };

  return (
    <div className="App">
      <div className="imageContainer">
        {flag && <img className="flag" src={flag} alt="Flag" />}
        <img className="mainPic" src={pic} alt={title} />

        <button className="arrowBtn arrowLeft">
          <img src={arrow_left} alt="Previous" />
        </button>
        <button className="arrowBtn arrowRight">
          <img src={arrow_right} alt="Next" />
        </button>
      </div>
      <div className="mainContent">
        <div className="topContent">
          <div className="leftContent">
            <div className="leftTopContent">
              <img className="icon" width="40" height="40" src={icon} />
              <div className="textLeftTopContent">
                <h1 className="title">{title}</h1>
                <p className="address">{address}</p>
              </div>
            </div>
            <div className="leftBotContent">
              <p className="labelTop">
                {project_type} · {year} · {ownership_type}
              </p>
              <p className="labelBot">{availabilities_label}</p>
            </div>
          </div>

          <div className="rightContent">
            <div className="price">
              <h1>{price}</h1>
            </div>
            <p className="priceFrom">{subprice_label}</p>
          </div>
        </div>

        <div className="bottomContent">
          <button onClick={toggleDescription}>
            {isDescriptionVisible ? "Hide description" : "See description"}
          </button>
          
          {/* Konten deskripsi yang ditampilkan secara kondisional */}
          {isDescriptionVisible && (
            <div className="descriptionContainer">
              <p className="description">
                {showRealNumbers ? description : anonymizedDescription}
              </p>
              {!showRealNumbers && (
                 <button className="revealButton" onClick={() => setShowRealNumbers(true)}>
                    Reveal phone numbers
                 </button>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
